sys_variable_value.value:
(function execute(inputs, outputs) {
/*
  Workspace Polling
    Finds any records not in an applied state then
    queries the Terraform Enterprise API for their status.
*/
gs.info("Polling Flow: terraform delete workspace started...");

// Input Variables
var tfRecord = inputs.tf_record;

// Create new objects from Terraform API SDK
var terraform_record = new tf_terraform_record();
var run              = new tf_run();
var workspace        = new tf_workspace();
var terraformRecord  = new tf_terraform_record();
var util             = new tf_util();

// Fetch Workspace Current Run Info
var runID        = null;
var runState     = null;
var tfCurrentRun = workspace.getWorkspaceCurrentRun(tfRecord.workspace_name);

if (tfCurrentRun !== null) {
  runID = tfCurrentRun.id;
}

if (runID !== null){
  runState = run.getRunState(runID).toLowerCase();
}

// Fetch Run State Info and update record
if (runState !== tfRecord.last_known_state &&
    tfCurrentRun !== null 
    ) {

  tfRecord.last_known_state = runState;
  tfRecord.update();

  // Post a comment with run state info to the request item
  var comment;
  switch (runState) {
  case "planned", "planned_and_finished":
    comment = util.formatCommentBlockQuote("Terraform: Plan Finished");
    break;

  case "applied":
    // Fetch workspace outputs once the run has been applied and include in comment
    comment = util.formatCommentBlockQuote("Terraform: Apply Finished");
    var outputs = workspace.getCurrentOutputs(tfRecord.workspace_id);
    if (outputs) {
      comment = comment + util.formatCommentOutputs(outputs);
    }
    break;

  case "pending", "confirmed", "errored", "canceled", "discarded":
    comment = util.formatCommentBlockQuote("Terraform: Run " + runState.capitalize());
    break;

  default:
    comment = util.formatCommentBlockQuote("Terraform: " + runState);
  }

  terraformRecord.postRITMComment(tfRecord.requested_item, comment);
}

gs.info("Polling Flow: terraform delete workspace finished.");

})(inputs, outputs);

