sys_variable_value.value:
(function execute(inputs, outputs) {

gs.info("Flow Action: Terraform Create Run Started...");

var terraform_record = new tf_terraform_record();
var run              = new tf_run();
var util             = new tf_util();

/* Variables from the SN Request */
var scRequestItem       = inputs.sc_req_item;
var scRequestItemNumber = inputs.sc_req_item.number;
var scRequestID         = inputs.sc_req;
var workspaceName       = inputs.workspace_name;
var description         = inputs.description;

var tfWorkspace       = terraform_record.get(workspaceName);
var workspaceID       = String(tfWorkspace.workspace_id);
var requestIDString   = "RequestID: " + scRequestID.number;

var content        = run.createRunRequestContent(workspaceID, requestIDString);
var requestContent = JSON.stringify(content);

/* HTTP REST request to Terraform API */
var http  = new tf_http("TF Runs", "Create Run", requestContent);
http.execute();

/* Add comment to original workspace request item */
var comment = util.formatCommentBlockQuote("Terraform: Planning Run...");
terraform_record.postRITMComment(tfWorkspace.requested_item, comment);

gs.info("Flow Action: Terraform Create Run Finished.");

})(inputs, outputs);

