sys_variable_value.value:
(function execute(inputs, outputs) {
/*
  This action looks for any ServiceNow Terraform records that have set
  is_destroyable to true. This assumes that these Terraform Workspaces have
  been marked for deletion.

  This action sets the is_destroyable value to true on the ServiceNow Terraform Table for the resource

  The "Poll Destroy Workspace" finishes the process of cleaning up the workspace by applying
  the destroy run, then deleting the workspace from Terraform.
*/

gs.info("Flow Action: terraform delete workspace started...");

var terraform_record = new tf_terraform_record();
var run              = new tf_run();
var util             = new tf_util();

var scRequest     = inputs.sc_req;
var tfRecord      = inputs.tf_record;
var workspaceName = tfRecord.workspace_name;

var tfWorkspace     = terraform_record.get(workspaceName);
var workspaceID     = String(tfWorkspace.workspace_id);
var requestIDString = "RequestID: " + scRequest.number;

// Mark the ServiceNow Terraform Resource Record as destroyable
tfRecord.is_destroyable = true;
tfRecord.update();

/* Plan a Destroy Run */
var destroyContent = run.createRunRequestContent(workspaceID, "Planning Destroy Run triggered by scheduler", true);
var destroyReqJSON = JSON.stringify(destroyContent);

// HTTP REST request to Terraform Run API to create a plan for the destroy run
var httpRun = new tf_http("TF Runs", "Create Run", destroyReqJSON);
httpRun.setParam("workspaceID", workspaceID);
httpRun.execute();

/* Add comment to original workspace request item */
var url = "sc_req_item.do?sys_id=" + current.sys_id;
var comment = util.formatCommentBlockQuote("Terraform: Marked Workspace for delete...");
terraform_record.postRITMComment(tfWorkspace.requested_item, comment);

/* Add comment to current request item */
url = "sc_req_item.do?sys_id=" + tfWorkspace.requested_item;
comment = "Marked Workspace for delete " + util.formatCommentHyperlink(url, workspaceName);
terraform_record.postRITMComment(current.sys_id, comment);

gs.info("Workflow: terraform delete workspace finished.");

})(inputs, outputs);

