sys_variable_value.value:
(function execute(inputs, outputs) {

gs.info("Flow Action: Terraform Apply Run Started...");

var run              = new tf_run();
var workspace        = new tf_workspace();
var terraform_record = new tf_terraform_record();
var util             = new tf_util();

var scRequestID      = inputs.sc_req;
var workspaceName    = inputs.workspace_name;

var runID;
var currentRun = workspace.getWorkspaceCurrentRun(workspaceName);
if (currentRun !== null) {
    runID = currentRun.id;
} else {
  gs.error("Run State is null for: " + scRequestID);
}

/* Data for the request content */
var tfWorkspace = terraform_record.get(workspaceName);
var workspaceID = String(tf_workspace.workspace_id);
var requestID   = "RequestID: " + scRequestID;

var content        = run.createRunRequestContent(workspaceID, scRequestID);
var requestContent = JSON.stringify(content);

/* HTTP REST request to Terraform API */
var http = new tf_http("TF Runs", "Apply Run", requestContent);
http.setParam("run_id", String(runID));
http.execute();

/* Add comment to original workspace request item */
var url     = "sc_req_item.do?sys_id=" + current.sys_id;
var comment = util.formatCommentBlockQuote("Terraform: Applying Run...");
terraform_record.postRITMComment(tfWorkspace.requested_item, comment);

gs.info("Flow Action: Terraform Apply Run Finished.");

})(inputs, outputs);

